        <!-- ============= Outdated Browser ============= -->
        <div id="outdated">
            <div class="window">
                <h6 class="subheadline-bold">Your browser is out-of-date</h6>
                <p class="body-text">Our website has detected that you are using a browser that will prevent you from accessing certain features. An upgrade is recommended.</p>
                <p><a id="btnUpdateBrowser" class="button" href="http://outdatedbrowser.com/" target="_blank">Update my browser now</a></p>
                <a href="#" id="btnCloseUpdateBrowser" title="Close">&times;</a>
            </div>
        </div>

        <!--[if IE 8]>
            <style>#outdated { display: block !important; } #btnCloseUpdateBrowser { display: none !important; }</style>
        <![endif]-->

        <!-- OUTDATED BROWSER plugin call -->
        <script>
            //event listener form DOM ready
            function addLoadEvent(func) {
                var oldonload = window.onload;
                if (typeof window.onload != 'function') {
                    window.onload = func;
                } else {
                    window.onload = function() {
                        if (oldonload) {
                            oldonload();
                        }
                        func();
                    }
                }
            }
            //call function after DOM ready
            // lower then ie11
            addLoadEvent(function(){
                outdatedBrowser({
                    // bgColor: '#ffffff',
                    // color: '#000000',
                    lowerThan: 'borderImage',  // < IE 11
                    languagePath: ''//'assets/js/libs/outdatedbrowser/lang/en.html'
                })
            });
        </script>        
        <!-- image configuration has to be called before the script loaded below -->
        <script>
            window.lazySizesConfig = window.lazySizesConfig || {};
            window.lazySizesConfig.init = false;
        </script>
